mapper.select:
select rnum, rno, no, content, writer, writeDate
from
(
	select rownum rnum, rno, no, content, writer, writeDate
	from(
		select rno, no, content, writer, writeDate
		from board_reply
		where no = #{no}
		order by rno desc
	)
)
where rnum between #{pageObject.startRow} and #{pageObject.endRow}
